import {
  MDBCol,
  MDBIcon,
  MDBNav,
  MDBNavItem,
  MDBNavLink,
  MDBRow,
  MDBTabContent,
  MDBTabPane,
  MDBBtn,
} from 'mdbreact';
import {inject, observer} from 'mobx-react';
import React, {Component} from 'react';
import {withRouter} from 'react-router-dom';
import OrderDetails from './OrderDetails';
import art from '../images/artworks/art1.png';
import Cart from './Cart';

class OrderTab extends Component {
  state = {
    activeItem: '1',
    selected: [],
    selectedCheckBox: [],
  };

  toggle = (tab) => (e) => {
    if (this.state.activeItem !== tab) {
      this.setState({
        activeItem: tab,
      });
    }
  };

  componentDidMount() {
    let {
      startingStore: {getOrderUser, getToCart},
    } = this.props;
    getOrderUser();
    getToCart();
  }

  setStatus = (status) => {
    this.setState({status: status});
  };

  render() {
    let {
      startingStore: {
        listOfSingleArtwork,
        listOfUserCart,
        listOfOrder,
        listofUserOrder,
        editOrder,
        listOfOrders,
      },
    } = this.props;
    let totalPrice = listOfUserCart.reduce(
      (total, item) => total + parseFloat(item.artworkPaymentAmount),
      0
    );

    let pendingOrders = listofUserOrder.filter((items) => {
      if (
        items.orderStatus === 'Delivery' ||
        items.orderStatus.includes('Pending')
      ) {
        return items;
      }
    });

    let listOfOrderReceived = listOfOrders.filter((Received) => {
      if (Received.orderStatus === 'Received') {
        return Received;
      }
    });

    let listOfOrderCancelled = listOfOrders.filter((Rejected) => {
      if (
        Rejected.orderStatus === 'Rejected' ||
        Rejected.orderStatus === 'PrintRejected'
      ) {
        return Rejected;
      }
    });

    return (
      <div className='ordercon'>
        <h4>My Orders</h4>
        <MDBNav className='nav-tabs'>
          <MDBNavItem>
            <MDBNavLink
              link
              to='#'
              active={this.state.activeItem === '1'}
              onClick={this.toggle('1')}
              role='tab'
            >
              My Cart
            </MDBNavLink>
          </MDBNavItem>
          <MDBNavItem>
            <MDBNavLink
              link
              to='#'
              active={this.state.activeItem === '2'}
              onClick={this.toggle('2')}
              role='tab'
            >
              Pending
            </MDBNavLink>
          </MDBNavItem>
          <MDBNavItem>
            <MDBNavLink
              link
              to='#'
              active={this.state.activeItem === '3'}
              onClick={this.toggle('3')}
              role='tab'
            >
              Completed
            </MDBNavLink>
          </MDBNavItem>
          <MDBNavItem>
            <MDBNavLink
              link
              to='#'
              active={this.state.activeItem === '4'}
              onClick={this.toggle('4')}
              role='tab'
            >
              Cancelled
            </MDBNavLink>
          </MDBNavItem>
          <MDBNavItem>
            <MDBNavLink
              link
              to='#'
              active={this.state.activeItem === '5'}
              onClick={this.toggle('5')}
              role='tab'
            >
              <MDBIcon icon='history' /> History
            </MDBNavLink>
          </MDBNavItem>
        </MDBNav>

        <MDBTabContent activeItem={this.state.activeItem}>
          <MDBTabPane tabId='1' role='tabpanel' className='cartab'>
            <div className='artlist'>
              <Cart cartData={listOfSingleArtwork[0]} />
            </div>
          </MDBTabPane>

          <MDBTabPane tabId='2' role='tabpanel' className='tabs'>
            <div className='artlist pending'>
              {pendingOrders.reverse().map((items, indexes) => (
                <div className='cartord'>
                  {items !== undefined
                    ? items.orderItems.map((item) => {
                        return (
                          <MDBRow className='artlistorder'>
                            <MDBCol md='3' className='artimg colpad'>
                              {item.artworkImg ? (
                                <img
                                  class='item-image'
                                  src={item.artworkImg}
                                  alt=''
                                />
                              ) : (
                                ''
                              )}
                            </MDBCol>
                            <MDBCol md='5' className='artdet colpad'>
                              <a
                                href='#!'
                                onClick={() => {
                                  this.props.history.push({
                                    pathname: `/Artist/${item.accFname} ${item.accLname}`,
                                    state: {
                                      artistName: `${item.accFname} ${item.accLname}`,
                                      accImg: this.state.artistImg,
                                      artistDescription: this.state
                                        .accDescription,
                                      accBday: this.state.accBday,
                                      accFollowers: this.state.accFollowers,
                                    },
                                  });
                                }}
                              >
                                {item.artistName} <MDBIcon icon='arrow-right' />
                              </a>
                              <br />
                              <p>"{item.artworkName}"</p>
                              <p>
                                Variations: {item.artworkMaterial},{' '}
                                {item.artworkSize}, {item.artworkFramingOptions}
                              </p>
                              <p>
                                &#8369;
                                {item.artworkPrice
                                  ? Number(
                                      parseFloat(item.artworkPrice)
                                    ).toLocaleString('en')
                                  : ''}
                              </p>
                            </MDBCol>
                            <MDBCol md='1' className='quan colpad'>
                              x{item.artworkQuantity}
                            </MDBCol>
                            <MDBCol md='3' className='colpad'>
                              <MDBRow className='sub'>
                                <MDBCol md='6' className='colpad2'>
                                  Subtotal
                                </MDBCol>
                                <MDBCol md='6' className='colpad2'>
                                  {item.artworkPaymentAmount
                                    ? parseFloat(
                                        item.artworkPaymentAmount
                                      ).toLocaleString('en-GB', {
                                        style: 'currency',
                                        currency: 'PHP',
                                      })
                                    : ''}
                                </MDBCol>
                              </MDBRow>
                            </MDBCol>
                          </MDBRow>
                        );
                      })
                    : ''}
                  <div className='acts clearfix' style={{marginBottom: '30px'}}>
                    {/* <MDBRow>
                         <MDBCol md="6" className="colpad2">Shipping Fee</MDBCol>
                         <MDBCol md="6" className="colpad2">
                           &#8369; 500
                         </MDBCol>
                       </MDBRow> */}
                    <MDBRow className='actsrow'>
                      <MDBCol md='5' className='total'>
                        TOTAL
                      </MDBCol>
                      <MDBCol md='7' className='total'>
                        &#8369;
                        {
                          +items.orderItems.reduce(
                            (a, b) =>
                              parseFloat(a) +
                              parseFloat(b.artworkPaymentAmount),
                            0
                          )
                        }
                      </MDBCol>
                      <MDBCol>
                        <div className='actsbtn'>
                          <MDBBtn
                            color='#fae933'
                            size='sm'
                            className='recieve'
                            onClick={() =>
                              editOrder(items._id, 'Received', items.accID)
                            }
                          >
                            Order Received
                          </MDBBtn>
                          <OrderDetails
                            pendingOrders={items}
                            steps={items.orderStatus}
                          />
                        </div>
                      </MDBCol>
                    </MDBRow>
                  </div>
                </div>
              ))}
            </div>
          </MDBTabPane>

          <MDBTabPane tabId='3' role='tabpanel' className='tabs complete'>
            <div className='artlist'>
              {listOfOrderReceived.reverse().map((items, indexes) => (
                <div className='cartord'>
                  {items !== undefined
                    ? items.orderItems.map((item) => {
                        return (
                          <MDBRow className='artlistorder'>
                            <MDBCol md='3' className='artimg colpad'>
                              {item.artworkImg ? (
                                <img
                                  class='item-image'
                                  src={item.artworkImg}
                                  alt=''
                                />
                              ) : (
                                ''
                              )}
                            </MDBCol>
                            <MDBCol md='5' className='artdet colpad'>
                              <a
                                href='#!'
                                onClick={() => {
                                  this.props.history.push({
                                    pathname: `/Artist/${item.accFname} ${item.accLname}`,
                                    state: {
                                      artistName: `${item.accFname} ${item.accLname}`,
                                      accImg: this.state.artistImg,
                                      artistDescription: this.state
                                        .accDescription,
                                      accBday: this.state.accBday,
                                      accFollowers: this.state.accFollowers,
                                    },
                                  });
                                }}
                              >
                                {item.artistName} <MDBIcon icon='arrow-right' />
                              </a>
                              <br />
                              <p>"{item.artworkName}"</p>
                              <p>
                                Variations: {item.artworkMaterial},{' '}
                                {item.artworkSize}, {item.artworkFramingOptions}
                              </p>
                              <p>
                                &#8369;
                                {item.artworkPrice
                                  ? Number(
                                      parseFloat(item.artworkPrice)
                                    ).toLocaleString('en-GB', {
                                      style: 'currency',
                                      currency: 'PHP',
                                    })
                                  : ''}
                              </p>
                            </MDBCol>
                            <MDBCol md='1' className='quan colpad'>
                              x{item.artworkQuantity}
                            </MDBCol>
                            <MDBCol md='3' className='colpad'>
                              <MDBRow className='sub'>
                                <MDBCol md='6' className='colpad2'>
                                  Subtotal
                                </MDBCol>
                                <MDBCol md='6' className='colpad2'>
                                  {item.artworkPaymentAmount
                                    ? parseFloat(
                                        item.artworkPaymentAmount
                                      ).toLocaleString('en-GB', {
                                        style: 'currency',
                                        currency: 'PHP',
                                      })
                                    : ''}
                                </MDBCol>
                              </MDBRow>
                            </MDBCol>
                          </MDBRow>
                        );
                      })
                    : ''}
                  <div className='acts clearfix' style={{marginBottom: '30px'}}>
                    {/* <MDBRow>
                         <MDBCol md="6" className="colpad2">Shipping Fee</MDBCol>
                         <MDBCol md="6" className="colpad2">
                           &#8369; 500
                         </MDBCol>
                       </MDBRow> */}
                    <MDBRow className='actsrow'>
                      <MDBCol md='5' className='total'>
                        TOTAL
                      </MDBCol>
                      <MDBCol md='7' className='total'>
                        &#8369;
                        {
                          +items.orderItems.reduce(
                            (a, b) =>
                              parseFloat(a) +
                              parseFloat(b.artworkPaymentAmount),
                            0
                          )
                        }
                      </MDBCol>
                      <MDBCol>
                        <div className='actsbtn'>
                          {/* <MDBBtn
                            color='#fae933'
                            size='sm'
                            className='recieve'
                            onClick={() =>
                              editOrder(items._id, 'Received', items.accID)
                            }
                          >
                            Order Received
                          </MDBBtn> */}
                          <OrderDetails
                            pendingOrders={items}
                            steps={items.orderStatus}
                          />
                        </div>
                      </MDBCol>
                    </MDBRow>
                  </div>
                </div>
              ))}
            </div>
          </MDBTabPane>

          <MDBTabPane tabId='4' role='tabpanel' className='tabs'>
            <div className='artlist cancelled'>
              {listOfOrderCancelled.reverse().map((items, indexes) => (
                <div className='cartord'>
                  {items !== undefined
                    ? items.orderItems.map((item) => {
                        return (
                          <MDBRow className='artlistorder'>
                            <MDBCol md='3' className='artimg colpad'>
                              {item.artworkImg ? (
                                <img
                                  class='item-image'
                                  src={item.artworkImg}
                                  alt=''
                                />
                              ) : (
                                ''
                              )}
                            </MDBCol>
                            <MDBCol md='5' className='artdet colpad'>
                              <a
                                href='#!'
                                onClick={() => {
                                  this.props.history.push({
                                    pathname: `/Artist/${item.accFname} ${item.accLname}`,
                                    state: {
                                      artistName: `${item.accFname} ${item.accLname}`,
                                      accImg: this.state.artistImg,
                                      artistDescription: this.state
                                        .accDescription,
                                      accBday: this.state.accBday,
                                      accFollowers: this.state.accFollowers,
                                    },
                                  });
                                }}
                              >
                                {item.artistName} <MDBIcon icon='arrow-right' />
                              </a>
                              <br />
                              <p>"{item.artworkName}"</p>
                              <p>
                                Variations: {item.artworkMaterial},{' '}
                                {item.artworkSize}, {item.artworkFramingOptions}
                              </p>
                              <p>
                                &#8369;
                                {item.artworkPrice
                                  ? Number(
                                      parseFloat(item.artworkPrice)
                                    ).toLocaleString('en')
                                  : ''}
                              </p>
                            </MDBCol>
                            <MDBCol md='1' className='quan colpad'>
                              x{item.artworkQuantity}
                            </MDBCol>
                            <MDBCol md='3' className='colpad'>
                              <MDBRow className='sub'>
                                <MDBCol md='6' className='colpad2'>
                                  Subtotal
                                </MDBCol>
                                <MDBCol md='6' className='colpad2'>
                                  {item.artworkPaymentAmount
                                    ? parseFloat(
                                        item.artworkPaymentAmount
                                      ).toLocaleString('en-GB', {
                                        style: 'currency',
                                        currency: 'PHP',
                                      })
                                    : ''}
                                </MDBCol>
                              </MDBRow>
                            </MDBCol>
                          </MDBRow>
                        );
                      })
                    : ''}
                  <div className='acts clearfix' style={{marginBottom: '30px'}}>
                    {/* <MDBRow>
                         <MDBCol md="6" className="colpad2">Shipping Fee</MDBCol>
                         <MDBCol md="6" className="colpad2">
                           &#8369; 500
                         </MDBCol>
                       </MDBRow> */}
                    <MDBRow className='actsrow'>
                      <MDBCol md='5' className='total'>
                        TOTAL
                      </MDBCol>
                      <MDBCol md='7' className='total'>
                        &#8369;
                        {
                          +items.orderItems.reduce(
                            (a, b) =>
                              parseFloat(a) +
                              parseFloat(b.artworkPaymentAmount),
                            0
                          )
                        }
                      </MDBCol>
                      <MDBCol>
                        <div className='actsbtn'>
                          {/* <MDBBtn
                            color='#fae933'
                            size='sm'
                            className='recieve'
                            onClick={() =>
                              editOrder(items._id, 'Received', items.accID)
                            }
                          >
                            Order Received
                          </MDBBtn> */}
                          <OrderDetails
                            pendingOrders={items}
                            steps={items.orderStatus}
                          />
                        </div>
                      </MDBCol>
                    </MDBRow>
                  </div>
                </div>
              ))}
            </div>
          </MDBTabPane>

          <MDBTabPane tabId='5' role='tabpanel' className='tabs'>
            <div className='artlist history'>
              {listofUserOrder.reverse().map((items, indexes) => (
                <div className='cartord'>
                  {items !== undefined
                    ? items.orderItems.map((item) => {
                        return (
                          <MDBRow className='artlistorder'>
                            <MDBCol md='3' className='artimg colpad'>
                              {item.artworkImg ? (
                                <img
                                  class='item-image'
                                  src={item.artworkImg}
                                  alt=''
                                />
                              ) : (
                                ''
                              )}
                            </MDBCol>
                            <MDBCol md='5' className='artdet colpad'>
                              <a
                                href='#!'
                                onClick={() => {
                                  this.props.history.push({
                                    pathname: `/Artist/${item.accFname} ${item.accLname}`,
                                    state: {
                                      artistName: `${item.accFname} ${item.accLname}`,
                                      accImg: this.state.artistImg,
                                      artistDescription: this.state
                                        .accDescription,
                                      accBday: this.state.accBday,
                                      accFollowers: this.state.accFollowers,
                                    },
                                  });
                                }}
                              >
                                {item.artistName} <MDBIcon icon='arrow-right' />
                              </a>
                              <br />
                              <p>"{item.artworkName}"</p>
                              <p>
                                Variations: {item.artworkMaterial},{' '}
                                {item.artworkSize}, {item.artworkFramingOptions}
                              </p>
                              <p>
                                &#8369;
                                {item.artworkPrice
                                  ? Number(
                                      parseFloat(item.artworkPrice)
                                    ).toLocaleString('en')
                                  : ''}
                              </p>
                            </MDBCol>
                            <MDBCol md='1' className='quan colpad'>
                              x{item.artworkQuantity}
                            </MDBCol>
                            <MDBCol md='3' className='colpad'>
                              <MDBRow className='sub'>
                                <MDBCol md='6' className='colpad2'>
                                  Subtotal
                                </MDBCol>
                                <MDBCol md='6' className='colpad2'>
                                  {item.artworkPaymentAmount
                                    ? parseFloat(
                                        item.artworkPaymentAmount
                                      ).toLocaleString('en-GB', {
                                        style: 'currency',
                                        currency: 'PHP',
                                      })
                                    : ''}
                                </MDBCol>
                              </MDBRow>
                            </MDBCol>
                          </MDBRow>
                        );
                      })
                    : ''}
                  <div className='acts clearfix' style={{marginBottom: '30px'}}>
                    {/* <MDBRow>
                         <MDBCol md="6" className="colpad2">Shipping Fee</MDBCol>
                         <MDBCol md="6" className="colpad2">
                           &#8369; 500
                         </MDBCol>
                       </MDBRow> */}
                    <MDBRow className='actsrow'>
                      <MDBCol md='5' className='total'>
                        TOTAL
                      </MDBCol>
                      <MDBCol md='7' className='total'>
                        &#8369;
                        {
                          +items.orderItems.reduce(
                            (a, b) =>
                              parseFloat(a) +
                              parseFloat(b.artworkPaymentAmount),
                            0
                          )
                        }
                      </MDBCol>
                      <MDBCol>
                        <div className='actsbtn'>
                          {/* <MDBBtn
                            color='#fae933'
                            size='sm'
                            className='recieve'
                            onClick={() =>
                              editOrder(items._id, 'Received', items.accID)
                            }
                          >
                            Order Received
                          </MDBBtn> */}
                          <OrderDetails
                            pendingOrders={items}
                            steps={items.orderStatus}
                          />
                        </div>
                      </MDBCol>
                    </MDBRow>
                  </div>
                </div>
              ))}
            </div>
          </MDBTabPane>
        </MDBTabContent>
      </div>
    );
  }
}

export default withRouter(inject('startingStore')(observer(OrderTab)));
